function iniciarMascaras() {
    $("input[alt=phone]").on('keyup', function () {
        if ($(this).val().length > 14) {
            $(this).setMask({ mask: "(99) 99999-9999", autoTab: false });
        } else {
            $(this).setMask({ mask: "(99) 9999-99999", autoTab: false });
        }
    });

    $("input[alt=phone-number]").on('keyup', function () {
        if ($(this).val().length > 8) {
            $(this).setMask({ mask: "9999-99999", autoTab: false });
        } else {
            $(this).setMask({ mask: "9999-9999", autoTab: false });
        }
    });

    $("input[alt=cnpf]").on('keyup', function () {
        if ($(this).val().length > 14) {
            $(this).setMask({ mask: "99.999.999/9999-99", autoTab: false });
        } else {
            $(this).setMask({ mask: "999.999.999-999", autoTab: false });
        }
    });
}